 */
export function startFileIngestion(callback?: (events: HookEvent[]) => void): void {
  console.log('ðŸš€ Starting file-based event streaming (in-memory only)');
  console.log('ðŸ“‚ Reading from ~/.claude/history/raw-outputs/');

