  let currentDir = process.cwd();
  for (let i = 0; i < 10; i++) {
    const claudeDir = join(currentDir, '.claude');
    if (existsSync(claudeDir)) {
      return currentDir;
    }
    const parentDir = dirname(currentDir);
    if (parentDir === currentDir) break; // Reached root
