  const month = String(pstDate.getMonth() + 1).padStart(2, '0');
  const day = String(pstDate.getDate()).padStart(2, '0');

  const monthDir = join(paiDir, '.claude', 'History', 'Raw-Outputs', `${year}-${month}`);
